sample_queries.query:
SELECT doctor_name, COUNT(DISTINCT patient_id) AS patient_count
FROM medical_records
GROUP BY doctor_name;

SELECT doctor_name, COUNT(DISTINCT patient_id) AS patient_count
FROM medical_records
GROUP BY doctor_name;

SELECT COUNT(DISTINCT patient_id) as sick_patients
FROM medical_records
WHERE diagnosis IS NOT NULL;

select p.patient_id, p.first_name, p.last_name, m.diagnosis
from patients p
join medical_records m on p.patient_id = m.patient_id;

SELECT fare, AVG(survived) AS survival_rate 
FROM titanic_train 
GROUP BY fare;
